mapper.select:
SELECT t1.id,t4.prize_name,t4.type,t4.prize_unit,t1.cash_time as cashTime,t1.member_phone,t1.member_name,t1.status,t1.sn_code,t3.score,t1.member_id,t5.address AS  addressName ,t1.address,t1.receive_type
from t_wx_activity_new_year_game_cash_prize_apply t1
LEFT JOIN t_wx_activity_new_year_game_play_record t3 on t1.record_id = t3.id
LEFT JOIN t_wx_activity_new_year_game_prize t4 on t1.prize_id = t4.id
LEFT JOIN t_wx_activity_new_year_game_address t5 on t1.address_id = t5.id
WHERE t1.main_id = #{actId}

	and t1.sn_code = #{snCode}


	and t4.type = #{type}


	and t1.status = #{status}

ORDER BY t3.score desc,t3.createtime asc

SELECT t1.id,t4.prize_name,t4.type,t4.prize_unit,t1.cash_time as cashTime,t1.member_phone,t1.member_name,t1.member_id,t1.status,t1.sn_code,t3.score
from t_wx_activity_new_year_game_cash_prize_apply t1
LEFT JOIN t_wx_activity_new_year_game_play_record t3 on t1.record_id = t3.id
LEFT JOIN t_wx_activity_new_year_game_prize t4 on t1.prize_id = t4.id
WHERE t1.main_id = #{actId}

	and t1.sn_code = #{snCode}


	and t4.type = #{type}


	and t1.status = #{status}

ORDER BY t3.score desc,t3.createtime asc
