mapper.select:
SELECT t1.id,t4.prize_name,t4.type,t4.prize_unit,t1.cash_time as cashTime,t1.member_phone,t1.status,t1.sn_code,t1.member_id,t5.address AS  addressName ,t1.address,t1.receive_type
from t_wx_activity_shakeluck_cash_prize_apply t1
LEFT JOIN t_wx_activity_shakeluck_prize t4 on t1.prize_id = t4.id
LEFT JOIN t_wx_activity_shakeluck_address t5 on t1.address_id = t5.id
WHERE t1.act_id = #{actId}

	and t1.sn_code = #{snCode}


	and t4.type = #{type}


	and t1.status = #{status}

ORDER BY t1.id DESC

SELECT t1.id,t4.prize_name,t4.type,t4.prize_unit,t1.cash_time as cashTime,t1.member_phone,t1.status,t1.sn_code,t1.member_id
from t_wx_activity_shakeluck_cash_prize_apply t1
LEFT JOIN t_wx_activity_shakeluck_prize t4 on t1.prize_id = t4.id
WHERE t1.act_id = #{actId}

	and t1.sn_code = #{snCode}


	and t4.type = #{type}


	and t1.status = #{status}

ORDER BY t1.id DESC
